    // );
  }


  return (
    <div className="App">
      <div>
        <button 
          type="button"
          onClick={ connectWallet }
        >
          connect wallet
        </button>
      </div>
      <div>description</div>
      <div>Mint Button</div>
    </div>
  );
}

export default App;
